import React, {useState, useEffect} from 'react';
import WeatherCard from './card/weather-card';
import Card from './card/card';
import {observer} from 'mobx-react-lite';

const CardRow = observer(() => {
  const [cardList, changeCardList] = useState(JSON.parse(window.localStorage.getItem('saved-locs')));

  let savedLocs = cardList;

  useEffect(() => {
    return () => {
      window.localStorage.setItem('saved-locs', JSON.stringify(savedLocs));
    }
  })
  
  const cachedWeather = async (savedLocs) => {
    let list = [];
    for (const loc of savedLocs) {
      if (typeof(loc) !== 'number') {
      }
    };
  };

  if (!!savedLocs && savedLocs.length > 0) {
    changeCardList(cachedWeather(savedLocs));
  } 
  
  return (
    <div 
      className="card-row body"
      // onMouseDown={handleMouseDown}
    >
      {!!savedLocs && savedLocs.length > 0 && 
        (cachedWeather())
      }
      <WeatherCard 
        loc={"Melbourne, AU"}
        index={0}
        data={{
          icon: '03n',
          desc: 'scattered clouds',
          current: 300.15,
          min: 301.15,
          max: 340.15,
          sunrise: 1485720272,
          sunset: 1485766550
        }}
      />
      {!(savedLocs.length >= 10) &&
        <Card loc={4163971} index={0}/>
      }
    </div>
  )
});

export default CardRow;
