import React, {useState} from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import Autocomplete from 'react-autocomplete';

const CardAdder = () => {
  const [isActive, setActive] = useState(false);
  const [location, setLocation] = useState('');
  const [value, setValue] = useState('');

  const cities=[
    { name: 'Melbourne',
      state: '',
      country: 'AU',
      id: 2158177,
    },
    { 
      name: 'Melbourne',
      state: 'FL',
      country: 'US',
      id: 4163971
    },
    {
      name: 'Sydney',
      state: '',
      country: 'AU',
      id: 2147714,
    },
    {
      name: 'Toronto',
      state: '',
      country: 'CA',
      id: 6167865,
    },
  ]

  const createCard = () => {
    setActive(!isActive);
  };

  const handleBlur = () => {
    setActive(false);
  }

  const handleSelect = (val) => {
    console.log(val);
    setLocation(val);
    setValue(val);
  }

  function createLabel({name, state, country, id}) {
    const bracket = state === '' ? '' : ` (${state})`;
    return `${name}${bracket}, ${country}`;
  }

  return (
    isActive ? 
      <div className="card weather-card">
        <Autocomplete
          autoFocus={true}
          getItemValue={(item) => createLabel(item)}
          items={cities}
          renderItem={(item, isHighlighted) => 
            <div style={{ background: isHighlighted ? 'lightblue' : 'gray'} }>
              {createLabel(item)}
            </div>
          }
          hiddenvalue={null}
          value={value}
          onChange={(e) => setValue(e.target.value)}
          onSelect={(val) => handleSelect(val)}
          onBlur={handleBlur}
          spellcheck={false}
        />
      </div>
    :
      <a className="card hover-card addButton" onClick={()=> createCard()}>
        <span id="add-text">
          <FontAwesomeIcon icon={['fas', 'plus-circle']} size="2x"/>
          <p>Add City</p>
        </span>
      </a>
  );
}

export default CardAdder;
