import React, {useState} from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import Autocomplete from 'react-autocomplete';

const CardAdder = () => {
  const [isActive, setActive] = useState(false);
  const [location, setLocation] = useState('');

  const cities=[
    { name: 'Melbourne',
      state: '',
      country: 'AU',
      id: 2158177,
    },
    { 
      name: 'Melbourne',
      state: 'FL',
      country: 'US',
      id: 4163971
    },
    {
      name: 'Sydney',
      state: '',
      country: 'AU',
      id: 2147714,
    },
    {
      name: 'Toronto',
      state: '',
      country: 'CA',
      id: 6167865,
    },
  ]

  const createCard = () => {
    setActive(!isActive);
  };

  function createLabel({name, state, country, id}) {
    const bracket = state == '' ? '' : `(${state})`;
    return `${name} ${bracket}, ${country}`;
  }

  return (
    isActive ? 
      <Autocomplete
        getItemValue={(item) => createLabel(item)}
        items={cities}
        renderItem={(item, isHighlighted) => 
          <div style={{ background: isHighlighted ? 'lightgray' : 'white'}}>
            {createLabel(item)}
          </div>
        }
      />
    :
      <a className="card hover-card addButton" onClick={()=> createCard()}>
        <span id="add-text">
          <FontAwesomeIcon icon={['fas', 'plus-circle']} size="2x"/>
          <p>Add City</p>
        </span>
      </a>
  );
}

export default CardAdder;
