import React from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import useGlobalState from '../../../services/configContext';
import './weather-card.css';

const WeatherCard = ({index, data, onDelete, onEdit}) => {
  const {
    name, country, icon, desc, current, min, max, pressure, humidity, vision, windspeed, winddeg,
  } = data || '';
  const [unit, changeUnit] = useGlobalState();

  const handleDelete = () => {};

  const convertTemp = (temp) => {
    let cel = temp - 273.15;
    return Math.round(unit === 'C' ? cel : cel*9/5 + 32);
  };

  const convertTime = (time) => {
    const date = new Date(time * 1000);
    let hours = date.getHours();
    const meridiem = hours < 12 ? 'am' : 'pm';
    if (hours > 12) hours -= 12;
    let min = date.getMinutes().toString();
    min = ('0' + min).slice(-2);

    return `${hours}:${min}${meridiem}`;
  };

  return !!name ? (
    <div className='card weather-card'>
      <div className='card-head'>
        <span>{name}</span>
        <a onClick={() => handleDelete()} >
          <FontAwesomeIcon icon={['fas', 'times']}/>
        </a>
      </div>
      <div className='card-body'>
        <div className='main'>
          <span className='current'>{convertTemp(current)}&#176;</span>
          <div className='status'>
            <img src={!!icon ? `http://openweathermap.org/img/wn/${icon}@2x.png` : ''} alt=""/>
            <span className='capitalize'>{desc}</span>
          </div>
        </div>
        <div classname='divider'/>
        <div className='min-max'>
          <div className='min'>
            <span>Min</span>
            <span>{convertTemp(min)}</span>
          </div>
          <div className='max'>
            <span>Max</span>
            <span>{convertTemp(max)}</span>
          </div>
        </div>
        <div style={styles.divider}/>
        <div className='pressure-humidity'>
          <div className='pressure'>
            <span>Pressure</span>
            <span>{pressure}hPa</span>
          </div>
          <div className='humid'>
            <span>Humidity</span>
            <span>{humidity}%</span>
          </div>
        </div>
        <div className='wind-vision'>
          <div className='wind'>
            <span>Windspeed</span>
            <span>
              {windspeed}m/s
              <div className='winddeg'>
                <FontAwesomeIcon icon={['fas', 'arrow-up']} />
              </div>
              </span>
          </div>
          <div className='vision'>
            <span>Vision</span>
            <span>{vision}m</span>
          </div>
        </div>
      </div> 
    </div>
  ) : (
    <div className="card weather-card" />
  );
};

export default WeatherCard;
