import React, { useState } from 'react';
import Switch from 'react-switch';
import useGlobalState from '../services/configContext'
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

// TODO: Figure out how to make global changes with changed state

const SideNavContent = () => {
  const [unit, changeUnit] = useGlobalState();

  const unitToBool = (unit) => {
    return unit === 'C' ? true : false;
  }

  const boolToUnit = (bool) => {
    return bool ? 'C' : 'F';
  }

  return (
    <div className="container">
      <label style={{width: '100%'}}>
        <span>Options</span>
        <a
						className="side-toggler"
						data-toggle="sideNav"
						data-target="#sideNav"
						style={{cursor: 'pointer', float: 'right'}}
            onClick={() => toggleSidebar()}
					>
						<FontAwesomeIcon icon={['fas', 'bars']} size="lg"/>
					</a>
      </label>
      <div className="divider" />
      <label style={{width: '100%'}}>
        <span>Unit</span>
        <Switch 
          className="sideNavSwitch"
          onChange={(checked) => boolToUnit(checked)}
          checked={unitToBool(unit)}
          
          style={{float:'right'}}> 
        </Switch>
      </label>
    </div>
  );
};

export default SideNavContent;
