import React from 'react';
import WeatherCard from './weather-card';
import {observer} from 'mobx-react-lite';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

const CardRow = observer(() => {
  const cachedLocs = JSON.parse(window.localStorage.getItem('saved-locs'));
  
  const cachedWeather = () => {
    if (!!cachedLocs && !!cachedLocs.length) {
      return cachedLocs.map(function(loc){
        return <WeatherCard savedLocs={loc} />
      })
    }
    return [];
  }
  
  return (
    <div className="row">
      {cachedWeather()}
      <a className="addButton">
        <span>
          <FontAwesomeIcon icon={['fas', 'plus-circle']} />
        </span>
      </a>
    </div>
  )
});

export default CardRow;
