import React from 'react';
import { observer } from 'mobx-react-lite';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import useGlobalState from './../../services/configContext.js';
import CitySearcher from './city-searcher';

const WeatherCard = observer(({loc, index, data}) => {
  const {icon, desc, current, min, max, sunrise, sunset} = data;
  
  const styles={
    divider:{
      margin: '8px 0',
      height: 1,
      backgroundColor: '#c0c0c066'
    }
  }

  const getImage = async() => {
    const imgUrl = `http://openweathermap.org/img/wn/${icon}@2x.png`;
    return (
      <img src={imgUrl} alt="" />
    )
  }

  const handleDelete = () => {};

  const convertTemp = (temp) => {};

  const convertTime = (time) => {};

  return (
    <div className='card weather-card' styles={{color: 'white'}}>
      <div className='card-head' styles={{fontSize: '3em'}}>
        <span>{loc}</span>
        <a onClick={() => handleDelete()} >
          <FontAwesomeIcon icon={['fas', 'times']}/>
        </a>
      </div>
      <div className='card-body'>
        <div className='main'>
          <span>{convertTemp(current)}&#176;</span>
          <div className='status'>
            {getImage}
            <span className='capitalize'>{desc}</span>
          </div>
        </div>
        <div style={styles.divider}/>
        <div className='min-max'>
          <div className='min'>
            <span>Min</span>
            <span>{convertTemp(min)}</span>
          </div>
          <div className='max'>
            <span>Max</span>
            <span>{convertTemp(max)}</span>
          </div>
        </div>
        <div style={styles.divider}/>
        <div className='sunrise-sunset'>
          <div className='rise'>
            <span>Sunrise</span>
            <span>{convertTime(sunrise)}</span>
          </div>
          <div className='set'>
            <span>Sunset</span>
            <span>{convertTime(sunset)}</span>
          </div>
        </div>
      </div>
    </div>
  );
});

export default WeatherCard;
