import React, {useState, useEffect} from 'react';
import Card from './card/card';
import Services from '../../services/services';
import 'lodash'

const CardRow = () => {
  const onDelete = (index) => {}

  const onAdd = async(data) => {
    await changeIdList(idList.concat([data]));
  };

  const savedWeather = async (savedList) => {
    const validIds = savedList.filter(id => typeof(id) == 'number');
    return await Promise.all(validIds.map((id) => Services.getData({locId: id})));
  };

  const [idList, changeIdList] = useState(() => {
    // const savedList = JSON.parse(window.localStorage.getItem('saved-ids'));
    // return !!savedList ? savedWeather(savedList) : [];
    return [];
  });

  useEffect(() => {
    console.log('idList', idList);
    window.localStorage.setItem('saved-ids', JSON.stringify(idList.map((data) => data.id)));
  });

  console.log(idList);
  console.log(idList.map((data, index) => <Card onDelete={(index) => onDelete(index)} onAdd={(data) => onAdd(data)} data={data} index={index}/>));
  
  return (
    <div 
      className="card-row body"
    >
      {!!idList && idList.length > 0 && 
        idList.map((data, index) => <Card onDelete={(index) => onDelete(index)} onAdd={(data) => onAdd(data)} data={data} index={index}/>)
      }
      {!!idList && idList.length < 10 &&
        <Card onDelete={(index) => onDelete(index)} onAdd={(data) => onAdd(data)} index={idList.length}/>
      }
    </div>
  )
};

export default CardRow;
