import React, {useState, useEffect} from 'react';
import Card from './card/card';
import Services from '../../services/services';

const CardRow = () => {
  const getCardWithData = async (id, index) => {
    const data = await Services.getData({locId: id});
    return <Card data={data} index={index} />
  }

  const cachedWeather = (savedList) => {
    const validIds = savedList.filter(id => typeof(id) == 'number');
    return validIds.map(async(id, index) => await getCardWithData(id, index));
  };

  const [idList, changeIdList] = useState(() => {
    const savedList = JSON.parse(window.localStorage.getItem('saved-ids'));
    return !!savedList ? cachedWeather(savedList) : [];
  });

  useEffect(() => {
    window.localStorage.setItem('saved-ids', JSON.stringify(idList));
  }, [])

  const onDelete = (index) => {}
  
  return (
    <div 
      className="card-row body"
      // onMouseDown={handleMouseDown}
    >
      {!!idList && idList.length > 0 && 
        [idList]
      }
      {!!idList && idList.length < 10 &&
        <Card index={idList.length} data={{}}/>
      }
    </div>
  )
};

export default CardRow;
