import React, {useState, useEffect} from 'react';
import WeatherCard from './card/weather-card';
import Card from './card/card';
import {observer} from 'mobx-react-lite';

const CardRow = observer(() => {
  const [idList, changeIdList] = useState(JSON.parse(window.localStorage.getItem('saved-ids')));

  let newIds = [];

  useEffect(() => {
    window.localStorage.setItem('saved-ids', JSON.stringify(idList));
  }, [idList])
  
  const cachedWeather = () => {
    newIds = idList.filter(id => typeof(id) == 'number');
    let list = [];
    return <Card loc={newIds[0]} index={0} />
    // return newIds.map((id, index) => <Card loc={id} index={index} />);
  };
  
  return (
    <div 
      className="card-row body"
      // onMouseDown={handleMouseDown}
    >
      {!!idList && idList.length > 0 && 
        (cachedWeather())
      }
      {newIds.length < 10 &&
        <Card index={newIds.length}/>
      }
    </div>
  )
});

export default CardRow;
