const fetch = require('node-fetch');

const Services = {
  prepareBody(data) {
    return JSON.stringify(data);
  },

  prepareUrl(path) {
    return 'http://localhost:1337' + path;
  },

  async post(url, body) {
    fetch(url, body).then(res => res.json()).then(res => res.send(res));
  },

  async getWeather(data) {
    const path = '/users';
    const url = this.prepareUrl(path);
    const body = this.prepareBody(data);
    console.log(url, body);
    await fetch(url, body).then(res => res.json()).then(res => console.log(res));
    //return await this.post(url, body);
  },
}

Services.getWeather(
  {locId:1}
);

module.exports = Services;
