const fetch = require('node-fetch');

const Services = {
  prepareBody(data) {
    return JSON.stringify(data);
  },

  prepareUrl(path) {
    return 'http://localhost:1337' + path;
  },

  async getWeather(locId) {
    const path = '/open-weather';
    console.log(locId);
    fetch("http://localhost:1337/open-weather", 
      { 
        method: 'POST', 
        body: {locId: locId}
      })
      .then(res => res.text())
      .then(res => console.log(res));
  }
}

Services.getWeather(
  {locId:1}
);

module.exports = Services;
