import React, {useState, useEffect } from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import { Autocomplete } from '@material-ui/lab';
import { TextField } from '@material-ui/core'

const CardAdder = () => {
  const [isActive, setActive] = useState(true);
  const [location, setLocation] = useState('');
  const [value, setValue] = useState('d');

  useEffect(() =>  {
    if (!!location) {

    }
  })

  const cities=[
    { name: 'Melbourne',
      state: '',
      country: 'AU',
      id: 2158177,
    },
    { 
      name: 'Melbourne',
      state: 'FL',
      country: 'US',
      id: 4163971
    },
    {
      name: 'Sydney',
      state: '',
      country: 'AU',
      id: 2147714,
    },
    {
      name: 'Toronto',
      state: '',
      country: 'CA',
      id: 6167865,
    },
  ]

  const handleSelect = (item) => {
    setLocation(item.id);
    setValue(createLabel(item));
  }

  function createLabel({name, state, country}) {
    const bracket = state === '' ? '' : ` (${state})`;
    return `${name}${bracket}, ${country}`;
  }

  return (
    isActive ? 
      <div className="card weather-card">
        <Autocomplete
          options={cities}
          getOptionLabel={(option) => createLabel(option)}
          autoHighlight
          style={{ width: '100%' }}
          renderInput={(params) => 
            <TextField 
              // {...params}
              autoFocus={true}
              onBlur={() => setActive(false)}
              style={ { width: '90%', paddingTop: '1rem'}}
              placeholder="Enter city name..."
              onChange={(event) => setValue(event.target.value)}
              value={value}
            />
          }
          onChange={(event, value) => handleSelect(value)}
        />
      </div>
    :
      <a className="card hover-card addButton" onClick={()=> setActive(true)}>
        <span id="add-text">
          <FontAwesomeIcon icon={['fas', 'plus-circle']} size="2x"/>
          <p>Add City</p>
        </span>
      </a>
  );
}

export default CardAdder;
