import React, {useState, useEffect} from 'react';
import WeatherCard from './card/weather-card';
import Card from './card/card';
import {observer} from 'mobx-react-lite';

const CardRow = observer(() => {
  const [idList, changeIdList] = useState(JSON.parse(window.localStorage.getItem('saved-ids')));

  let newIds = [];

  useEffect(() => {
    return () => {
      window.localStorage.setItem('saved-ids', JSON.stringify(newIds));
    }
  })
  
  const cachedWeather = () => {
    newIds = idList.filter(id => typeof(id) == 'number');
    return newIds.map((id, index) => <Card loc={id} index={index} />);
  };
  
  return (
    <div 
      className="card-row body"
      // onMouseDown={handleMouseDown}
    >
      {!!idList && idList.length > 0 && 
        (cachedWeather())
      }
      <WeatherCard 
        loc={"Melbourne, AU"}
        index={0}
        data={{
          icon: '03n',
          desc: 'scattered clouds',
          current: 300.15,
          min: 301.15,
          max: 340.15,
          sunrise: 1485720272,
          sunset: 1485766550
        }}
      />
      {!(idList.length >= 10) &&
        <Card loc={4163971} index={0}/>
      }
    </div>
  )
});

export default CardRow;
