import React from 'react';
import WeatherCard from './weather-card';
import {observer} from 'mobx-react-lite';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

const CardRow = observer(() => {
  const cachedLocs = JSON.parse(window.localStorage.getItem('saved-locs'));
  
  const cachedWeather = () => {
    if (!!cachedLocs && !!cachedLocs.length) {
      return cachedLocs.slice(60).map(function(loc, index){
        return <WeatherCard params={loc, index} />
      })
    }
    return [];
  }

  const createCard = 'hello';
  
  return (
    <div className="row">
      {!!cachedLocs && cachedLocs.length > 0 && 
        cachedWeather()
      }
      <a className="addButton" onClick={()=> createCard()}>
        <span>
          <FontAwesomeIcon icon={['fas', 'plus-circle']} />
        </span>
      </a>
    </div>
  )
});

export default CardRow;
