import React from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import useGlobalState from '../../../services/configContext';
import './weather-card.css';

const WeatherCard = ({index, data, onDelete, onEdit}) => {
  const {
    name, country, icon, desc, current, min, max, pressure, humidity, vision, windspeed, winddeg,
  } = data || '';
  const [unit, changeUnit] = useGlobalState();

  const handleDelete = () => {};

  const styles = {
    divider: {
      margin: '8px 0',
      height: 1,
      backgroundColor: '#c0c0c066',
    },
    rotate: {
      rotate: 90,
    }
  };

  const convertTemp = (temp) => {
    let cel = temp - 273.15;
    return Math.round(unit === 'C' ? cel : cel*9/5 + 32);
  };

  const convertTime = (time) => {
    const date = new Date(time * 1000);
    let hours = date.getHours();
    const meridiem = hours < 12 ? 'am' : 'pm';
    if (hours > 12) hours -= 12;
    let min = date.getMinutes().toString();
    min = ('0' + min).slice(-2);

    return `${hours}:${min}${meridiem}`;
  };

  return !!name ? (
    <div className='card weather-card'>
      <div className='card-head'>
        <span className='name'>{name}</span>
        <a className='button' onClick={() => handleDelete()} >
          <FontAwesomeIcon icon={['fas', 'times']}/>
        </a>
      </div>
      <div className='card-body'>
        <div className='main'>
          <span className='current'>{convertTemp(current)}&#176;</span>
          <div className='status'>
            <img id='img' src={!!icon ? `http://openweathermap.org/img/wn/${icon}@2x.png` : ''} alt=""/>
            <span id='desc' className='capitalize'>{desc}</span>
          </div>
        </div>
        <div style={styles.divider}/>
        <div className='min-max'>
          <div className='contain-left'>
            <span>Min</span>
            <span className='stat'>{convertTemp(min)}</span>
          </div>
          <div className='contain-right'>
            <span>Max</span>
            <span className='stat'>{convertTemp(max)}</span>
          </div>
        </div>
        <div style={styles.divider}/>
        <div className='container'>
          <div className='contain-left'>
            <span>Windspeed</span>
            <span className='stat'>
              {windspeed}m/s
              <div style={styles.rotate}>
                <FontAwesomeIcon icon={['fas', 'arrow-up']} />
              </div>
              </span>
          </div>
          <div className='contain-right'>
            <span>Vision</span>
            <span className='stat'>{vision}m</span>
          </div>
        </div>
        <div style={styles.divider}/>
        <div className='container'>
          <div className='contain-left'>
            <span>Pressure</span>
            <span className='stat'>{pressure}hPa</span>
          </div>
          <div className='contain-right'>
            <span>Humidity</span>
            <span className='stat'>{humidity}%</span>
          </div>
        </div>
      </div> 
    </div>
  ) : (
    <div className="card weather-card" />
  );
};

export default WeatherCard;
