import React, {useState, useEffect } from 'react';
import { Autocomplete } from '@material-ui/lab';
import { TextField, CircularProgress } from '@material-ui/core';
import 'node-fetch';

const CitySearcher = ({onSubmit, onBlur}) => {
  const [value, setValue] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() =>  {
    if (!loading) return; 

    (async (location) => {
      const response = await fetch('https://country.register.gov.uk/records.json?page-size=5000');
      const data = await response.json();
    })();

    setLocation(false);
  });

  const cities=[
    { name: 'Melbourne',
      country: 'AU',
      id: 2158177,
    },
    { 
      name: 'Melbourne',
      country: 'US',
      id: 4163971,
    },
    {
      name: 'Sydney',
      country: 'AU',
      id: 2147714,
    },
    {
      name: 'Toronto',
      country: 'CA',
      id: 6167865,
    },
  ]

  function createLabel({name, state, country}) {
    return `${name}, ${country}`;
  }

  return (
    <div className="card weather-card">
      <Autocomplete
        options={cities}
        getOptionLabel={(option) => createLabel(option)}
        autoHighlight
        style={{ width: '100%' }}
        loading={loading}
        renderInput={(params) => 
          <TextField 
            {...params}
            value={value}
            autoFocus={true}
            // onBlur={() => setActive(false)}
            style={ { width: '90%', paddingTop: '1rem'}}
            placeholder="Enter city name..."
            onChange={(event) => setValue(event.target.value)}
            InputProps={{
              ...params.InputProps,
              endAdornment: (
                <React.Fragment>
                  {loading ? <CircularProgress color="inherit" size={20} /> : null}
                  {params.InputProps.endAdornment}
                </React.Fragment>
              )
            }}
          />
        }
        onChange={(e, value) => setLocation(value.id)}
      />
    </div>
  );
}

export default CitySearcher;
