import React from 'react';
import { observer } from 'mobx-react-lite';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import useGlobalState from './../../services/configContext.js';
import CitySearcher from './city-searcher';

const WeatherCard = observer(({loc, index, data}) => {
  const {icon, desc, current, min, max, sunrise, sunset} = data;
  const city = loc.split(',')[0];
  const [unit, changeUnit] = useGlobalState();
  
  const styles={
    divider: {
      margin: '8px 0',
      height: 1,
      backgroundColor: '#c0c0c066',
    },
    cardhead: {
      fontSize: 'x-large',
    },
    status: {
      textAlign: 'center',
      float: 'right',
    }
  }

  const handleDelete = () => {};

  const convertTemp = (temp) => {
    let cel = temp - 273.15;
    return Math.round(unit === 'C' ? cel : cel*9/5 + 32);
  };

  const convertTime = (time) => {
    const date = new Date(time * 1000);
    let hours = date.getHours();
    const meridiem = hours < 12 ? 'am' : 'pm';
    if (hours > 12) hours -= 12;
    let min = date.getMinutes().toString();
    min = ('0' + min).slice(-2);

    return `${hours}:${min}${meridiem}`;
  };

  return (
    <div className='card weather-card'>
      <div className='card-head'>
        <span>{city}</span>
        <a onClick={() => handleDelete()} >
          <FontAwesomeIcon icon={['fas', 'times']}/>
        </a>
      </div>
      <div className='card-body'>
        <div className='main'>
          <span style={styles.current}>{convertTemp(current)}&#176;</span>
          <div className='status' style={styles.status}>
            <img src={`http://openweathermap.org/img/wn/${icon}@2x.png`} alt=""/>
            <span className='capitalize'>{desc}</span>
          </div>
        </div>
        <div style={styles.divider}/>
        <div className='min-max'>
          <div className='min'>
            <span>Min</span>
            <span>{convertTemp(min)}</span>
          </div>
          <div className='max'>
            <span>Max</span>
            <span>{convertTemp(max)}</span>
          </div>
        </div>
        <div style={styles.divider}/>
        <div className='sunrise-sunset'>
          <div className='rise'>
            <span>Sunrise</span>
            <span>{convertTime(sunrise)}</span>
          </div>
          <div className='set'>
            <span>Sunset</span>
            <span>{convertTime(sunset)}</span>
          </div>
        </div>
      </div>
    </div>
  );
});

export default WeatherCard;
