import React, {useState, useEffect } from 'react';
import { Autocomplete } from '@material-ui/lab';
import { TextField, CircularProgress } from '@material-ui/core'

const CitySearcher = ({initVal, activeState}) => {
  const [location, setLocation] = useState(0);
  const [value, setValue] = useState(initVal);
  const loading = !!location;

  useEffect(() =>  {
    if (!location) return; 

    (async (location) => {
      const response = await fetch('https://country.register.gov.uk/records.json?page-size=5000');
      const data = await response.json();
      await setTimeout(console.log(data), 5000);
    })();

    setLocation(false);
  });

  const cities=[
    { name: 'Melbourne',
      state: '',
      country: 'AU',
      id: 2172797,
    },
    { 
      name: 'Melbourne',
      state: 'FL',
      country: 'US',
      id: 4163971
    },
    {
      name: 'Sydney',
      state: '',
      country: 'AU',
      id: 2147714,
    },
    {
      name: 'Toronto',
      state: '',
      country: 'CA',
      id: 6167865,
    },
  ]

  function createLabel({name, state, country}) {
    const bracket = state === '' ? '' : ` (${state})`;
    return `${name}${bracket}, ${country}`;
  }

  return (
    <div className="card weather-card">
      <Autocomplete
        options={cities}
        getOptionLabel={(option) => createLabel(option)}
        autoHighlight
        style={{ width: '100%' }}
        loading={loading}
        renderInput={(params) => 
          <TextField 
            {...params}
            value={value}
            autoFocus={true}
            // onBlur={() => setActive(false)}
            style={ { width: '90%', paddingTop: '1rem'}}
            placeholder="Enter city name..."
            onChange={(event) => setValue(event.target.value)}
            InputProps={{
              ...params.InputProps,
              endAdornment: (
                <React.Fragment>
                  {loading ? <CircularProgress color="inherit" size={20} /> : null}
                  {params.InputProps.endAdornment}
                </React.Fragment>
              )
            }}
          />
        }
        onChange={(e, value) => setLocation(value.id)}
      />
    </div>
  );
}

export default CitySearcher;
