import React, {useState, useEffect } from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import CitySearcher from './city-searcher';
import WeatherCard from './weather-card';
import Services from '../../../services/services';

const Card = ({loc, index}) => {
  const [isActive, setActive] = useState(false);
  const [data, setData] = useState({});
  let name = '';
  let loading = false;

  const [location, setLocation] = useState(loc);
  useEffect(() => {
      if (!location) return;
      console.log('hi');
      loading = true;
      async function fetchData() {
        setData(await Services.getWeather({locId: loc}));
        loading = false;
      };
      //fetchData();
      setActive(false);
    }, [location])

  return (
    isActive ? 
      <CitySearcher loading={loading} />
    : !!location ?
      <WeatherCard name={name} index={index} data={data} />
      :
      // eslint-disable-next-line jsx-a11y/anchor-is-valid
      <a className="card hover-card addButton" onClick={()=> setActive(true)} >
        <span id="add-text">
          <FontAwesomeIcon style={{paddingTop:'60%', width: '100%', display: 'inline-block'}} icon={['fas', 'plus-circle']} size="2x"/>
          <p>Add City</p>
        </span>
      </a>
  );
}

export default Card;
