import React, {useState} from 'react';
import WeatherCard from './card/weather-card';
import CardAdder from './card/card-adder';
import {observer} from 'mobx-react-lite';

const CardRow = observer(() => {
  const [cardList, changeCardList] = useState([]);

  const savedLocs = JSON.parse(window.localStorage.getItem('saved-locs'));
  
  const cachedWeather = (savedLocs) => {
    return savedLocs.slice(60).map(function(loc, index){
      return <WeatherCard params={loc, index} />
    });
  }

  if (!!savedLocs && savedLocs.length > 0) {
    changeCardList(cachedWeather(savedLocs));
  } 
  
  console.log(savedLocs.length);
  return (
    <div 
      className="card-row body"
      // onMouseDown={handleMouseDown}
    >
      {!!savedLocs && savedLocs.length > 0 && 
        cachedWeather()
      }
      {!(savedLocs.length >= 60) &&
        <CardAdder />
      }
    </div>
  )
});

export default CardRow;
