import React, {useState, createContext, useContext} from 'react';
import './App.css';

import Background from './components/layout/background';
import Navbar from './components/layout/top-navbar';
import CardRow from './components/util/card-row';

// Create Global var Unit -> Changes in option are reflected through app
const savedUnit = !!window.localStorage.getItem('unit-degree') ? window.localStorage.getItem('unit-degree') : 'C'
const GlobalStateContext = createContext(savedUnit);
const DispatchStateContext = createContext(undefined);

const GlobalStateProvider = ({children}) => {
  const [unit, changeUnit] = useState(savedUnit)
  return (
    <GlobalStateContext.Provider value={unit}>
      <DispatchStateContext.Provider value={changeUnit}>
        {children}
      </DispatchStateContext.Provider>
    </GlobalStateContext.Provider>
  )
};

const App = () => {
  return (
    <GlobalStateProvider>
      <div className="App">
          <Background />
          <Navbar />
          <CardRow />
      </div>
    </GlobalStateProvider>
  );
}

export default App;
