import React from 'react';
import WeatherCard from './weather-card';
import {observer} from 'mobx-react-lite';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

const CardRow = observer(() => {
  const savedLocs = JSON.parse(window.localStorage.getItem('saved-locs'));
  
  const cachedWeather = () => {
    return savedLocs.slice(60).map(function(loc, index){
      return <WeatherCard params={loc, index} />
    });
  }

  const createCard = () => {
    console.log('click');
  };
  
  console.log(savedLocs);
  return (
    <div className="card-row body">
      {!!savedLocs && savedLocs.length > 0 && 
        cachedWeather()
      }
      {!(savedLocs.length > 60) &&
        // eslint-disable-next-line jsx-a11y/anchor-is-valid
        <a className="card hover-card addButton" onClick={()=> createCard()}>
          <span id="add-text">
            <FontAwesomeIcon icon={['fas', 'plus-circle']} size="2x"/>
            <p>Add City</p>
          </span>
        </a>
      }
    </div>
  )
});

export default CardRow;
