import React, {useState, useReducer, useEffect } from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import CitySearcher from './city-searcher';
import WeatherCard from './weather-card';
import Services from '../../../services/services';

const Card = ({loc="", index}) => {
  const [state, setState] = useReducer(
    (state, newState) => ({...state, ...newState}),
    {isActive: false, data: {id: loc}}
  );

  useEffect(() => {
      if (!state.isActive || state.data.id==="") return;

      async function fetchData() {
        setState({
          isActive: false, 
          data: await Services.getWeather({locId: state.data.id})
        });
      };
      fetchData();

    }, [state.data]);

  const {isActive, data} = state;

  return (
    isActive ? 
      <CitySearcher data={loc}/>
    : data !== {} ?
      <WeatherCard index={index} data={data} />
      :
      // eslint-disable-next-line jsx-a11y/anchor-is-valid
      <a className="card hover-card addButton" onClick={()=> setActive(true)} >
        <span id="add-text">
          <FontAwesomeIcon style={{paddingTop:'60%', width: '100%', display: 'inline-block'}} icon={['fas', 'plus-circle']} size="2x"/>
          <p>Add City</p>
        </span>
      </a>
  );
}

export default Card;
