import React, { useEffect } from 'react';
import Switch from 'react-switch';
import useGlobalState from '../services/configContext'

// TODO: Figure out how to make global changes with changed state

const SideNavContent = () => {
  const [unit, changeUnit] = useGlobalState();

  useEffect(() => {
    window.localStorage.setItem('degree-unit', unit);
  }, [unit]);

  const unitToBool = (unit) => {
    return unit === 'C' ? true : false;
  }

  const boolToUnit = (bool) => {
    return bool ? 'C' : 'F';
  }

  const styles={
    divider:{
      margin: '8px 0',
      height: 1,
      backgroundColor: '#c0c0c066'
    }, 
    text:{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 25,
        paddingRight: 2
    }
  }

  return (
    <div>
      <label style={{width: '100%'}}>
        <h3>Options</h3>
      </label>
      <div style={styles.divider} />
      <div style={{width: '100%'}}>
        <b>Unit</b>
        <Switch
          checked={unitToBool(unit)}
          onChange={(checked) => changeUnit(boolToUnit(checked))}
          handleDiameter={28}
          offColor="#00539c"
          onColor="#eea47f"
          offHandleColor="#eea47f"
          onHandleColor="#00539c"
          height={40}
          width={70}
          className="react-switch"
          id="small-radius-switch"
          checkedIcon={<b style={styles.text}>C</b>}
          uncheckedIcon={<b style={styles.text}>F</b>}
        />
      </div>
    </div>
  );
};

export default SideNavContent;
