import React from 'react';
import WeatherCard from './weather-card';
import {observer} from 'mobx-react-lite';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

const CardRow = observer(() => {
  const savedLocs = JSON.parse(window.localStorage.getItem('saved-locs'));

  const handleMouseDown = ({ target, clientX }) => {
    if (target.className !== "card-row") {
      return;
    }
    window.addEventListener("mousemove", this.handleMouseMove);
    window.addEventListener("mouseup", this.handleMouseUp);
    this.setState({
      startX: clientX,
      startScrollX: window.scrollX
    });
  };

  // Go to new scroll position every time the mouse moves while dragging is activated
  const handleMouseMove = ({ clientX }) => {
    const { startX, startScrollX } = this.state;
    const scrollX = startScrollX - clientX + startX;
    window.scrollTo(scrollX, 0);
    const windowScrollX = window.scrollX;
    if (scrollX !== windowScrollX) {
      this.setState({
        startX: clientX + windowScrollX - startScrollX
      });
    }
  };

  // Remove drag event listeners
  const handleMouseUp = () => {
    if (this.state.startX) {
      window.removeEventListener("mousemove", this.handleMouseMove);
      window.removeEventListener("mouseup", this.handleMouseUp);
      this.setState({ startX: null, startScrollX: null });
    }
  };
  
  const cachedWeather = () => {
    return savedLocs.slice(60).map(function(loc, index){
      return <WeatherCard params={loc, index} />
    });
  }

  const createCard = () => {
    console.log('click');
  };
  
  console.log(savedLocs.length);
  return (
    <div 
      className="card-row body"
      onMouseDown={handleMouseDown}
    >
      {!!savedLocs && savedLocs.length > 0 && 
        cachedWeather()
      }
      {!(savedLocs.length >= 60) &&
        // eslint-disable-next-line jsx-a11y/anchor-is-valid
        <a className="card hover-card addButton" onClick={()=> createCard()}>
          <span id="add-text">
            <FontAwesomeIcon icon={['fas', 'plus-circle']} size="2x"/>
            <p>Add City</p>
          </span>
        </a>
      }
    </div>
  )
});

export default CardRow;
