import React from 'react';
import './App.css';

import Background from './components/layout/background';
import Navbar from './components/layout/top-navbar';
import CardRow from './components/util/card-row';

function App() {
  const GlobalState = {
    unit: !!window.localStorage.getItem('unit-degree') ? window.localStorage.getItem('unit-degree') : 'C'
  }

  return (
    <div className="App">
      <Background />
      <Navbar />
      <CardRow />
    </div>
  );
}

export default App;
